import React, { useEffect, useRef, useState } from "react";
import "./index.css";
import logo from "./assets/logo.png";
import image1 from "./assets/image.jpg";
import portfolioImg from "./assets/business.jpg";

const App = () => {
  const aboutRef = useRef(null);

  // Typewriter effect states
  const languages = ["C++", "C", "Python", "Java"];
  const [displayedText, setDisplayedText] = useState("");
  const [langIndex, setLangIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  // Typewriter logic
  useEffect(() => {
    const currentLang = languages[langIndex];
    let timeout;

    if (charIndex <= currentLang.length) {
      timeout = setTimeout(() => {
        setDisplayedText(currentLang.slice(0, charIndex));
        setCharIndex(charIndex + 1);
      }, 150);
    } else {
      timeout = setTimeout(() => {
        setCharIndex(0);
        setLangIndex((prev) => (prev + 1) % languages.length);
      }, 1000);
    }

    return () => clearTimeout(timeout);
  }, [charIndex, langIndex]);

  // Intersection observer
  useEffect(() => {
  const observer = new IntersectionObserver(
    ([entry]) => {
      if (aboutRef.current) {
        if (entry.isIntersecting) {
          aboutRef.current.classList.add("show");
        } else {
          aboutRef.current.classList.remove("show"); // Remove when not in view
        }
      }
    },
    { threshold: 0.3 }
  );

  if (aboutRef.current) {
    observer.observe(aboutRef.current);
  }

  return () => {
    if (aboutRef.current) {
      observer.unobserve(aboutRef.current);
    }
  };
}, []);



const featuresRef = useRef(null);

useEffect(() => {
  const observer = new IntersectionObserver(
    ([entry]) => {
      if (entry.isIntersecting) {
        featuresRef.current.classList.add("appear");
      } else {
        featuresRef.current.classList.remove("appear");
      }
    },
    { threshold: 0.3 }
  );

  const currentRef = featuresRef.current;
  if (currentRef) observer.observe(currentRef);

  return () => {
    if (currentRef) observer.unobserve(currentRef);
  };
}, []);




  return (
    <>
      <div className="header">
        <div className="containner">
          <h1>{'{R.}'} Ravi Programming Academy</h1>
        </div>
      </div>

      <div className="landing" style={{ backgroundImage: `url(${image1})` }}>
        <div className="intro-text">
          <h1>Power Up with Programming</h1>
          <p>"Compile Dreams. Execute Success."</p>
          <p>
            Learn to Program <span className="typewriter">{displayedText}</span>
          </p>
        </div>
      </div>

      <div className="features" ref={featuresRef}>
        <div className="container">
          <div className="feat">
            <i className="fas fa-magic fa-3x"></i>
            <h3>Managing Finances</h3>
            <p>Effortlessly track, manage, and grow your finances in one place.</p>
          </div>
          <div className="feat">
            <i className="far fa-gem fa-3x"></i>
            <h3>Features & Benefits</h3>
            <p>Seamlessly track expenses and categorize your spending. Sync multiple accounts and manage all your finances from one dashboard.</p>
          </div>
          <div className="feat">
            <i className="fas fa-globe-asia fa-3x"></i>
            <h3>Powerful Tools for Smarter Money Management.</h3>
            <p>Get access to budgeting, goal tracking, and automated bill reminders to stay on top of your finances.</p>
          </div>
        </div>
      </div>

      <div className="services" id="services">
        <div className="container">
